mapper.select:
SELECT customer.customer_id customerId , customer.first_name firstName , customer.last_name lastName , customer.store_id storeId , customer.email email , customer.active active , V1.address address , V1.district district , V1.city city , V1.country country FROM customer INNER join (SELECT address.address_id addressId , address.address address , address.district district , V0.city city , V0.country country FROM address INNER JOIN (SELECT city.city_id , city.city , country.country FROM city INNER JOIN country ON city.country_id = country.country_id) V0 ON address.city_id = V0.city_id) V1 ON customer.address_id = V1.addressId
WHERE first_name LIKE CONCAT('%', '#{searchWord}', '%') OR last_name LIKE CONCAT('%', '#{searchWord}', '%')
ORDER BY firstName, lastName LIMIT #{beginRow}, #{rowPerPage}
